//  Problem: https://leetcode.com/problems/substring-with-concatenation-of-all-words/
//You are given a string, s, and a list of words, words, that are all of the same length. Find all starting indices of substring(s) in s that is a concatenation of each word in words exactly once and without any intervening characters.
//
//For example, given:
//s: "barfoothefoobarman"
//words: ["foo", "bar"]
//
//You should return the indices: [0,9].
//(order does not matter).

#include <iostream>
#include <vector>
#include <map>

using namespace std;

class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        vector<int> result;
        map<string, int> hash;
        for_each(words.begin(), words.end(), [&](string word) {
            map<string, int>::iterator iter = hash.find(word);
            if (hash.end() == iter) {
                hash[word] = 1;
            } else {
                hash[word]++;
            }
        });
        
        map<string, int> isExist;
        
        int num = (int)words.size();
        if (num == 0) {
            return result;
        }
        int size = (int)s.size();
        int len = (int)words[0].size();
        if (size < num*len) {
            return result;
        }
        for (int i = 0; i < size; i++) {
            int index = i;
            int count = 0;
            for (int j = i; j + len <= size; j += len) {
                string sub = s.substr(j, len);
                map<string, int>::iterator iter = hash.find(sub);
                if (hash.end() != iter) {
                    map<string, int>::iterator it = isExist.find(sub);
                    if (isExist.end() == it) {
                        isExist[sub] = 1;
                    } else if (it->second < iter->second) {
                        isExist[sub]++;
                    } else {
                        isExist.clear();
                        count = 0;
                        break;
                    }
                    count++;
                    if (num == count) {
                        result.push_back(index);
                        isExist.clear();
                        count = 0;
                        break;
                    }
                } else {
                    isExist.clear();
                    count = 0;
                    break;
                }
            }
        }
        
        return result;
    }
};

void printVector(vector<int> v) {
    for_each(v.begin(), v.end(), [](int n) {
        cout << n << ", ";
    });
    cout << endl;
}

